import React, { useState } from 'react';
import './App.css';

function App() {
  const [activeFolder, setActiveFolder] = useState(null);

  const handleFolderClick = (folder) => {
    setActiveFolder(folder);
  };

  return (
    <div className="App">
      <div className="folders">
        <div
          className={`folder ${activeFolder === 'folder1' ? 'active' : ''}`}
          onClick={() => handleFolderClick('folder1')}
        >
          Folder 1
        </div>
        <div
          className={`folder ${activeFolder === 'folder2' ? 'active' : ''}`}
          onClick={() => handleFolderClick('folder2')}
        >
          Folder 2
        </div>
      </div>
      <div className="content">
        {activeFolder === 'folder1' && (
          <div className="folder-content">
            <p>Content of Folder 1</p>
          </div>
        )}
        {activeFolder === 'folder2' && (
          <div className="folder-content">
            <p>Content of Folder 2</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
