import React, { useState } from 'react';
import './App.css';

function App() {
  const mainFolders = [
    {
      name: 'Main Folder 1',
      subfolders: [
        {
          name: 'Subfolder 1',
          files: ['File 1', 'File 2', 'File 3', 'File 4', 'File 5'],
        },
        {
          name: 'Subfolder 2',
          files: ['File 6', 'File 7', 'File 8', 'File 9', 'File 10'],
        },
        // ... Add more subfolders as needed
      ],
    },
    {
      name: 'Main Folder 2',
      subfolders: [
        // ... Add subfolders and files as needed
      ],
    },
    // ... Add more main folders as needed
  ];

  const [activeMainFolder, setActiveMainFolder] = useState(null);
  const [activeSubFolder, setActiveSubFolder] = useState(null);

  const handleMainFolderClick = (mainFolder) => {
    setActiveMainFolder(mainFolder);
    setActiveSubFolder(null); // Reset subfolder selection when a main folder is clicked.
  };

  const handleSubFolderClick = (subFolder) => {
    setActiveSubFolder(subFolder);
  };

  return (
    <div className="App">
      <div className="main-folders">
        {mainFolders.map((mainFolder) => (
          <div
            key={mainFolder.name}
            className={`folder ${activeMainFolder === mainFolder.name ? 'active' : ''}`}
            onClick={() => handleMainFolderClick(mainFolder.name)}
          >
            {mainFolder.name}
            <div className="sub-folders">
        {activeMainFolder &&
          mainFolders
            .find((mainFolder) => mainFolder.name === activeMainFolder)
            .subfolders.map((subFolder) => (
              <div
                key={subFolder.name}
                className={`folder ${activeSubFolder === subFolder.name ? 'active' : ''}`}
                onClick={() => handleSubFolderClick(subFolder.name)}
              >
                {subFolder.name}
                <div className="files">
        {activeSubFolder &&
          mainFolders
            .find((mainFolder) => mainFolder.name === activeMainFolder)
            .subfolders.find((subFolder) => subFolder.name === activeSubFolder).files.map((file) => (
              <div key={file} className="file">
                {file}
              </div>
            ))}
      </div>
              </div>
            ))}
      </div>
          </div>
        ))}
      </div>


    </div>
  );
}

export default App;
