import React, { useState } from 'react';
import './App.css';

function App() {
  const mainFolders = ['Main Folder 1', 'Main Folder 2', 'Main Folder 3', 'Main Folder 4', 'Main Folder 5'];
  const [activeMainFolder, setActiveMainFolder] = useState(null);
  const [activeSubFolder, setActiveSubFolder] = useState(null);

  const handleMainFolderClick = (mainFolder) => {
    setActiveMainFolder(mainFolder);
    setActiveSubFolder(null); // Reset subfolder selection when a main folder is clicked.
  };

  const handleSubFolderClick = (subFolder) => {
    setActiveSubFolder(subFolder);
  };

  return (
    <div className="App">
      <div className="main-folders">
        {mainFolders.map((mainFolder, index) => (
          <div
            key={mainFolder}
            className={`folder ${activeMainFolder === mainFolder ? 'active' : ''}`}
            onClick={() => handleMainFolderClick(mainFolder)}
          >
            {mainFolder}
          </div>
        ))}
      </div>
      <div className="sub-folders">
        {activeMainFolder && Array.from({ length: 5 }, (_, index) => {
          const subFolder = `Sub Folder ${index + 1}`;
          return (
            <div
              key={subFolder}
              className={`folder ${activeSubFolder === subFolder ? 'active' : ''}`}
              onClick={() => handleSubFolderClick(subFolder)}
            >
              {subFolder}
            </div>
          );
        })}
      </div>
      <div className="content">
        {activeSubFolder && (
          <div className="folder-content">
            <p>Content of {activeSubFolder}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
