import React, { useState } from 'react';
import './App.css';

function App() {
  const [activeMainFolder, setActiveMainFolder] = useState(null);
  const [activeSubFolder, setActiveSubFolder] = useState(null);

  const handleMainFolderClick = (mainFolder) => {
    setActiveMainFolder(mainFolder);
    setActiveSubFolder(null); // Reset subfolder selection when a main folder is clicked.
  };

  const handleSubFolderClick = (subFolder) => {
    setActiveSubFolder(subFolder);
  };

  return (
    <div className="App">
      <div className="main-folders">
        {Array.from({ length: 5 }, (_, index) => {
          const folderNumber = index + 1;
          const folderId = `main-folder${folderNumber}`;
          return (
            <div
              key={folderId}
              className={`folder ${activeMainFolder === folderId ? 'active' : ''}`}
              onClick={() => handleMainFolderClick(folderId)}
            >
              Main Folder {folderNumber}
            </div>
          );
        })}
      </div>
      <div className="sub-folders">
        {activeMainFolder && (
          Array.from({ length: 5 }, (_, index) => {
            const subFolderNumber = index + 1;
            const subFolderId = `sub-folder${subFolderNumber}`;
            return (
              <div
                key={subFolderId}
                className={`folder ${activeSubFolder === subFolderId ? 'active' : ''}`}
                onClick={() => handleSubFolderClick(subFolderId)}
              >
                Sub Folder {subFolderNumber}
              </div>
            );
          })
        )}
      </div>
      <div className="content">
        {activeSubFolder && (
          <div className="folder-content">
            <p>Content of {activeSubFolder}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
