// MainFolder.js
import React, { useState } from 'react';
import Subfolder from './subFolder';

const MainFolder = ({ mainFolder }) => {
  const [isChecked, setIsChecked] = useState(false);
  const [activeMain,setActiveMain]= useState(null);

  const toggleCheckbox = () => {
    setIsChecked(!isChecked);
  };

  const handleActiveMain=()=>{
    setActiveMain(mainFolder.name);
    console.log(activeMain,"is clicked");
  }

  return (
    <div onClick={handleActiveMain}>
      <input type="checkbox" checked={isChecked} onChange={toggleCheckbox} />
      <h3>{mainFolder.name}</h3>
      {activeMain.name === mainFolder.name &&  mainFolder.subfolders.map((subfolder, index) => (
        <Subfolder key={index} subfolder={subfolder} isChecked={isChecked} />
      ))}
    </div>
  );
};

export default MainFolder;
